import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

ReactDOM.render(<h1>Hello, my friend!</h1>, document.getElementById('root'));

ReactDOM.render(
  <ul>
    <li>Item 1</li>
    <li>Item 2</li>
  </ul>,
  document.getElementById('list'));


function Navbar() {
  return (
    <nav class="navbar navbar-expand-lg navbar-light bg-light">
  <div class="container-fluid">
    <a class="navbar-brand" href="https://sanrothodi.com">Navbar</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="https://sanrothodi.com">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="https://sanrothodi.com">Link</a>
        </li>
        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle" href="https://sanrothodi.com" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            Dropdown
          </a>
          <ul class="dropdown-menu" aria-labelledby="navbarDropdown">
            <li><a class="dropdown-item" href="https://sanrothodi.com">Action</a></li>
            <li><a class="dropdown-item" href="https://sanrothodi.com">Another action</a></li>
            <li><hr class="dropdown-divider" /></li>
            <li><a class="dropdown-item" href="https://sanrothodi.com">Something else here</a></li>
          </ul>
        </li>
      </ul>
      <form class="d-flex">
        <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
        <button class="btn btn-outline-success" type="submit">Search</button>
      </form>
    </div>
  </div>
</nav>
  )
};


function TitleOfThisSite() {
  return (
    <h1>Title Of This Site</h1>
  )
};

ReactDOM.render(
  <div>
    <Navbar />
    <TitleOfThisSite />
  </div>,
  document.getElementById("navbar")
);

// Example on how to render items on vanilla JS
let header = document.createElement('h1');
let headerText = document.createTextNode('This is JS 1');
let headerElement = header.appendChild(headerText);
let h1ID = document.getElementById('h1ID');

h1ID.appendChild(headerElement);

// Or we could also do:
let h1 = document.createElement('h1');
h1.textContent = 'This is JS 2'
h1.className = 'header'
document.getElementById('h1ID').appendChild(h1)

// With JSX we can write this in one single line
ReactDOM.render(<h1 className="header">This is JSX</h1>, document.getElementById('h1ID2'));


// We can also store our html in variables to work more comfortably

let smallHeader = (
  <div>
    <h1>Small header</h1>
    <p>This is a small header</p>
  </div>
)

ReactDOM.render(smallHeader, document.getElementById('smallHeader'))

console.log(smallHeader); // As you can see the contents of your JSX is translated to JavaScript as an object







ReactDOM.render()
